import axios from "axios";

const API_BASE_URL = 'http://localhost:8080'

// NOTE withCredentials : false 일때 서버에 요청을 보낼 때 쿠키(인증정보)를 포함해 보내지 않음,
// 서버가 응답한 쿠기를 저장하지도 않음
const authApi = async ev => {
  login: async (username, password) => {

    try{
      axios.post(`{API_BASE_URL}/auth/login`, {
        username: username,
        password, password
      }, {withCredentials: true})
    }
    catch (e){
      console.error(e);
    }

    logout: async () => {

    }

  }

  // ev.preventDefault() // 중요: 브라우저의 기본 제출 동작을 막음
}
export default authApi()
