import {create} from 'zustand'
import { persist } from 'zustand/middleware'

// fixme
const useAuthStore = create()(
    persist(
        (set) => ({
          isLoggedIn:false,
          username:"",
          roles:[],
          accessToken:"",

          login: (claims, token) => set({
            isLoggedIn:true,
            username: claims.sub,
            roles: token.roles,
            accessToken: token
          }),
          logout: () => set({
            isLoggedIn:false,
            username:"",
            roles:[],
            accessToken:""
          })
        }),

        { name: 'position-storage' },
    ),
)

export default useAuthStore
